mapper.select:
select
r.id,r.issueId,r.title,r.creator,r.createDate,r.modifier,i.detail,r.planDate,r.endDate
from report r,issuestate i
where r.state = i.sid

	AND issueId like concat('%', #{issueid}, '%')


	AND state like concat('%', #{state}, '%')


	AND creator like concat('%', #{creator}, '%')


	AND modifier like concat('%', #{modifier}, '%')



	AND createdate > #{createStartDate}


	AND createdate < #{createEndDate}


	AND createdate between #{createStartDate} and
	#{createEndDate}



	AND enddate
	between #{endStartDate} and #{endEndDate}


	AND enddate
	> #{endStartDate}


	AND enddate
	< #{endEndDate}

SELECT user.id, user.userId, user.name, (SELECT COUNT(report.id) FROM report WHERE report.creator=user.userId) as createNum , (SELECT COUNT(report.id) FROM report WHERE report.modifier=user.userId) as modifiNum , (SELECT COUNT(report.id) FROM report WHERE report.modifier=user.userId and report.state = 3) as finishNum, (SELECT COUNT(report.id) FROM report WHERE report.modifier=user.userId and report.state = 3)/(SELECT COUNT(report.id) FROM report WHERE report.modifier=user.userId) as completeRate FROM user,report WHERE user.permission = 1
AND user.userId like concat('%', #{userid}, '%') AND user.name like concat('%', #{name}, '%') AND user.userId like concat('%', #{userid}, '%') AND user.name like concat('%', #{name}, '%')
GROUP BY user.id;
